INSERT INTO `igs6l_overrider` (`id`, `constant`, `string`, `file`) VALUES
(1756, 'COM_INSTALLER_MSG_WARNINGS_PHPUPLOADNOTSETDESC', 'Каталог временных файлов PHP предназначен для хранения загруженных файлов в то время, когда они уже получены интерпретатором языка PHP, но ещё не переданы Joomla!. Если в параметрах PHP этот каталог не указан, это ещё не означает, что загрузка файлов не будет работать. Если у вас всё же возникли проблемы в данном вопросе, следует указать путь к каталогу временных файлов в php.ini.', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
(1757, 'COM_INSTALLER_MSG_WARNINGS_PHPUPLOADNOTWRITEABLE', 'Каталог для временных файлов, указанный в параметрах PHP, недоступен для записи.', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
(1758, 'COM_INSTALLER_MSG_WARNINGS_PHPUPLOADNOTWRITEABLEDESC', 'Каталог временных файлов PHP недоступен для записи. Это может привести к проблемам при загрузке расширений. Если у вас возникли проблемы при загрузке расширений, проверьте ''%s'' и установите на него права на запись. после этого проверьте, устранена ли проблема.', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
(1759, 'COM_INSTALLER_MSG_WARNINGS_SMALLPOSTSIZE', 'Ограничение на объем данных POST-запроса', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
(1760, 'COM_INSTALLER_MSG_WARNINGS_SMALLPOSTSIZEDESC', 'Ограничение объёма данных, передаваемых в POST-запросе, определяем общее количество информации, которую можно будет передать на сервер. Это ограничение распространяется на формы добавления статей, изображений и расширений. В данный момент установлено значение, меньшее чем 2MB. Это может создать проблемы при сохранении больших статей или загрузке расширений. Отредактируйте значение переменной post_max_size в файле php.ini', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
(1761, 'COM_INSTALLER_MSG_WARNINGS_SMALLUPLOADSIZE', 'Ограничение на объем загружаемых файлов', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
(1762, 'COM_INSTALLER_MSG_WARNINGS_SMALLUPLOADSIZEDESC', 'Максимальный объем загружаемых файлов менее 2MB. Это может создать проблемы при загрузке больших расширений.', 'C:\\OpenServer\\domains\\Joomla346\\administrator\\language\\ru-RU\\ru-RU.com_installer.ini'),
